mapper.select:
SELECT COUNT(m.mc_id) FROM  t_member_card c INNER JOIN t_wx_bus_member m ON c.`mc_id`=m.`mc_id`
where c.busId=#{busId}

	 and c.ct_id =#{ctId}

select  mc_id  from  t_member_card where busId=#{busId} and ct_id in

	#{ctId}

and gt_id in

	#{gtId}

SELECT COUNT(c.mc_id) AS countMcId,c.gt_id,g.gt_grade_name FROM t_member_card c
INNER JOIN t_member_gradetype g ON c.gt_id=g.gt_id WHERE c.busId=#{busId}

	and c.ct_id=#{ctId}

GROUP BY gt_id

SELECT DATE_FORMAT(receiveDate, '%Y-%m-%d') AS receiveDate, COUNT(mc_id) AS countId FROM t_member_card WHERE  busId=#{busId}
GROUP BY DATE_FORMAT(receiveDate, '%Y-%m-%d')
ORDER BY receiveDate DESC  LIMIT 7
